package com.example.android.mama;import android.os.Bundle;import android.support.design.widget.TabLayout;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.Toolbar;import com.example.android.mama.fragments.SimpleFragmentPagerAdapter;/** * Created by Micaela on 06/12/2017. */public class MainActivity extends AppCompatActivity{    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        final Toolbar myToolBar = (Toolbar) findViewById(R.id.my_toolbar);        setSupportActionBar(myToolBar);        final ViewPager viewPager = (ViewPager) findViewById(R.id.viewpager);        final SimpleFragmentPagerAdapter adapter = new SimpleFragmentPagerAdapter(getSupportFragmentManager());        viewPager.setAdapter(adapter);        getSupportActionBar().setTitle(adapter.getPageTitle(0));        TabLayout tabLayout = (TabLayout) findViewById(R.id.tabs);        tabLayout.setupWithViewPager(viewPager);        viewPager.setOnPageChangeListener(new ViewPager.OnPageChangeListener() {            @Override            public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {            }            @Override            public void onPageSelected(int position) {                myToolBar.setTitle(adapter.getPageTitle(position));            }            @Override            public void onPageScrollStateChanged(int state) {            }        });    }}