package com.example.android.mama.fragments;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;/** * Created by Micaela on 18/12/2017. */public class SimpleFragmentPagerAdapter extends FragmentPagerAdapter {    final int PAGE_COUNT = 3;    private String tabTitles[] = new String[] {"Inicio", "Tarjetas", "Gastos Fijos"};    public SimpleFragmentPagerAdapter(FragmentManager fm) {super(fm);}    @Override    public Fragment getItem(int position) {        if(position == 0){            return new InicioFragment();        }else if (position == 1){            return new TarjetasFragment();        }else{            return new GastosFijosFragment();        }    }    @Override    public int getCount() {        return PAGE_COUNT;    }    @Override    public CharSequence getPageTitle(int position) {        return tabTitles[position];    }}